mapper.select:
select 

from shopping_cart
where user_id = #{userId,jdbcType=VARCHAR}
  and goods_id = #{goodsId,jdbcType=VARCHAR}

mapper.delete:
delete from shopping_cart
where user_id = #{userId,jdbcType=VARCHAR}
  and goods_id = #{goodsId,jdbcType=VARCHAR}

mapper.insert:
insert into shopping_cart (goods_id, goods_price, goods_number
  )
values (#{goodsId,jdbcType=VARCHAR}, #{goodsPrice,jdbcType=DOUBLE}, #{goodsNumber,jdbcType=INTEGER}
  )

mapper.update:
update shopping_cart


    goods_price = #{goodsPrice,jdbcType=DOUBLE},


    goods_number = #{goodsNumber,jdbcType=INTEGER},


where user_id = #{userId,jdbcType=VARCHAR}
  and goods_id = #{goodsId,jdbcType=VARCHAR}

update shopping_cart
set goods_price = #{goodsPrice,jdbcType=DOUBLE},
  goods_number = #{goodsNumber,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=VARCHAR}
  and goods_id = #{goodsId,jdbcType=VARCHAR}
